var uuid = require('node-uuid');
module.exports = function(server) {
//  var ledQueryChi = server.from('chicago').where({ type: 'led' });
  var ledQueryDet = server.from('peer-1').where({ type: 'led' });
  server.observe([ledQueryDet], function(det) {

    var stream = det.createReadStream('logs');
    stream.on('data', function(data) {
      console.log('Peer-1 Led: ' + data.transition, ' at ', new Date(data.timestamp))
    });
  });

};
